    return(
        <div>
            <form>
                <input type="text" onChange={(e)=>handle(e)} value={this.state.eventDetails.name} placeholder="Event name" name="hello"/>
                <input type="text" onChange={(e)=>handle(e)} value={this.state.eventDetails.description} placeholder="Description"/>
                <input type="date" onChange={(e)=>handle(e)} value={this.state.eventDetails.date} placeholder="" min="2021-07-25" max="2021-08-25"/>
